// Process the blog content
const item = $input.item.json;

// Extract all the fields
const title = item['Blog Title'] || '';
const metaDescription = item['Meta Description'] || '';
const author = item.Author || 'Azani Team';
const content = item['Content Body'] || '';
const categoriesAndTags = item['Categories & Tags'] || '';
const slug = item['Slug/Permalink'] || '';
const imageUrls = item['Image URLs'] || '';
const publishDate = item['Publish Date'] || new Date().toISOString().split('T')[0];

// Process the content (convert markdown to HTML if needed)
let processedContent = content;

// Check if content is markdown and convert if necessary
if (content.includes('##') || content.includes('*')) {
  // This is a simple check - in a production environment, 
  // you might want to use a proper markdown parser like 'marked'
  processedContent = content
    .replace(/^# (.+)$/gm, '<h1>$1</h1>')
    .replace(/^## (.+)$/gm, '<h2>$1</h2>')
    .replace(/^### (.+)$/gm, '<h3>$1</h3>')
    .replace(/\*\*(.+?)\*\*/g, '<strong>$1</strong>')
    .replace(/\*(.+?)\*/g, '<em>$1</em>')
    .replace(/\[(.+?)\]\((.+?)\)/g, '<a href="$2">$1</a>')
    .replace(/(?:<li>.*<\/li>)/gs, match => `<ul>${match}</ul>`)
    .replace(/\n\n/g, '</p><p>');

  processedContent = `<p>${processedContent}</p>`;
}

// Process image URLs
let imageUrlsArray = [];
if (imageUrls && imageUrls.includes(',')) {
  imageUrlsArray = imageUrls.split(',').map(url => url.trim());
} else if (imageUrls) {
  imageUrlsArray = [imageUrls.trim()];
}

// Validate slug
let cleanSlug = slug;
if (!cleanSlug) {
  // Generate slug from title
  cleanSlug = title.toLowerCase()
    .replace(/[^\w\s-]/g, '') // Remove special chars
    .replace(/\s+/g, '-') // Replace spaces with hyphens
    .replace(/-+/g, '-'); // Remove consecutive hyphens
}

// Create a structured blog post object
const blogPost = {
  title,
  metaDescription,
  author,
  content: processedContent,
  categoriesAndTags,
  slug: cleanSlug,
  imageUrls: imageUrlsArray,
  publishDate,
  status: 'Approved',
  processedAt: new Date().toISOString()
};

// Return the processed blog post
return [{json: blogPost}];

