// Extract the AI-generated content
const response = $input.item.json;
const item = $('Google Sheets Trigger').item.json;

let generatedContent = '';

// Check if the response has the expected structure
if (response.candidates && response.candidates[0] && response.candidates[0].content && response.candidates[0].content.parts && response.candidates[0].content.parts[0]) {
  generatedContent = response.candidates[0].content.parts[0].text;
} else {
  throw new Error('Unexpected API response structure from Gemini');
}

// Generate meta description from first paragraph
let metaDescription = '';
const firstParagraph = generatedContent.match(/<p>(.*?)<\/p>/s);
if (firstParagraph && firstParagraph[1]) {
  // Strip HTML tags from first paragraph and limit to 160 chars
  metaDescription = firstParagraph[1].replace(/<[^>]*>/g, '').substring(0, 160);
  if (metaDescription.length === 160) metaDescription += '...';
}

// Extract all the fields
const title = item['Blog Title'] || '';
const author = item.Author || 'Arzani Team';
const categoriesAndTags = item['Categories & Tags'] || '';
const slug = item['Slug/Permalink'] || '';
const imageUrls = item['Image URLs'] || '';
const publishDate = item['Publish Date'] || new Date().toISOString().split('T')[0];

// Process image URLs
let imageUrlsArray = [];
if (imageUrls && imageUrls.includes(',')) {
  imageUrlsArray = imageUrls.split(',').map(url => url.trim());
} else if (imageUrls) {
  imageUrlsArray = [imageUrls.trim()];
}

// Validate slug
let cleanSlug = slug;
if (!cleanSlug) {
  // Generate slug from title
  cleanSlug = title.toLowerCase()
    .replace(/[^\w\s-]/g, '') // Remove special chars
    .replace(/\s+/g, '-') // Replace spaces with hyphens
    .replace(/-+/g, '-'); // Remove consecutive hyphens
}

// Create a structured blog post object
const blogPost = {
  title,
  metaDescription: metaDescription || item['Meta Description'] || `${title} - Insights from Arzani's expert business marketplace`,
  author,
  content: generatedContent,
  categoriesAndTags,
  slug: cleanSlug,
  imageUrls: imageUrlsArray,
  publishDate,
  status: 'AI Generated',
  processedAt: new Date().toISOString(),
  aiGenerated: true
};

// Return the processed blog post
return {json: blogPost};

// Function to create consistent email templates
function createEmailTemplate(type, data) {
  // Common styling
  const styles = `
    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
    .container { max-width: 600px; margin: 0 auto; padding: 20px; }
    .header { text-align: center; margin-bottom: 30px; }
    .header img { max-width: 150px; }
    .content { background-color: #f9f9f9; padding: 20px; border-radius: 5px; }
    .preview { background-color: white; padding: 15px; border-left: 4px solid #316FD4; margin: 20px 0; }
    .button { display: inline-block; padding: 10px 20px; background-color: #316FD4; color: white !important; text-decoration: none; border-radius: 5px; margin-right: 10px; }
    .button.secondary { background-color: #777; }
    .button-container { margin: 25px 0; }
    .footer { margin-top: 30px; font-size: 12px; color: #777; text-align: center; }
  `;

  // Common header and footer
  const header = `
    <div class="header">
      <img src="https://arzani.co.uk/figma design exports/logos/white-arzani-logo.svg" alt="Arzani Logo">
    </div>
  `;

  const footer = `
    <div class="footer">
      <p> ${new Date().getFullYear()} Arzani. All rights reserved.</p>
      <p>If you have any questions, please contact our support team.</p>
    </div>
  `;

  // Template-specific content
  let content = '';

  if (type === 'published') {
    content = `
      <div class="content">
        <h1>Blog Post Published!</h1>
        <p>Your blog post has been successfully published to the Arzani website.</p>
        <p><strong>Title:</strong> ${data.title}</p>
        <p><strong>Author:</strong> ${data.author}</p>
        <p><strong>Publication Date:</strong> ${data.publishDate}</p>
        <p>You can view your published post by clicking the button below:</p>
        <p><a href="https://arzani.co.uk/blog/${data.slug}" class="button">View Published Post</a></p>
      </div>
    `;
  } else if (type === 'review') {
    content = `
      <div class="content">
        <h1>AI-Generated Blog Post Ready for Review</h1>
        <p>An AI-generated blog post is ready for your review before publishing.</p>
        <p><strong>Title:</strong> ${data.title}</p>
        <p><strong>Meta Description:</strong> ${data.metaDescription}</p>
        <div class="preview">
          <h3>Content Preview:</h3>
          <p>${data.content.substring(0, 300)}...</p>
        </div>
        <div class="button-container">
          <a href="https://n8n.arzani.co.uk/webhook/ai-content-approval?action=approve&id=${data.rowNumber}" class="button">Approve & Publish</a>
          <a href="https://n8n.arzani.co.uk/webhook/ai-content-approval?action=reject&id=${data.rowNumber}" class="button secondary">Reject</a>
        </div>
        <p>You can also view and edit the full content in the Google Sheet before making a decision.</p>
      </div>
    `;
  }

  // Assemble the complete HTML email
  return `<!DOCTYPE html>
<html>
<head>
  <style>${styles}</style>
</head>
<body>
  <div class="container">
    ${header}
    ${content}
    ${footer}
  </div>
</body>
</html>`;
}

// Get input data
const inputData = $input.item.json;

// Get sheet trigger data
const triggerData = $('Google Sheets Trigger').item.json;

// Determine which template to use based on the node connection
const templateType = $('Process AI Content') ? 'review' : 'published';

// Prepare data for the template
const templateData = {
  title: inputData.title,
  author: inputData.author,
  publishDate: inputData.publishDate,
  slug: inputData.slug,
  content: inputData.content,
  metaDescription: inputData.metaDescription,
  rowNumber: triggerData.row_number
};

// Generate the appropriate email template
const emailTemplate = createEmailTemplate(templateType, templateData);

// Return the email template along with all original data
return {
  json: {
    ...inputData,
    emailTemplate
  }
};

